var logoImg = 'data:image/jpeg;base64,';


var doc = new jsPDF();
var page = 1;

function AddZero(num) {
    //Pad given value to the left with "0"
    return (num >= 0 && num < 10) ? "0" + num : num + "";
}


function createCover(name,attention,memory,executivefunctions,language,difficulty)
{
doc = new jsPDF();
page= 1;

var loremipsum = "La 'NeuroRehabLab Task Generator' è un software che è stato sviluppato con la collaborazione di oltre 20 professionisti della salute che utilizza modelli di calcolo della funzione cognitiva per offrire una formazione altamente personalizzato per ogni individuo. Le attività create con la 'NeuroRehabLab Task Generator' sono generati proceduralmente, cioè, sono sempre diverso ogni volta che vengono creati.\n\nVisita http://neurorehabilitation.m-iti.org per ulteriori informazioni";

var gray=230;

//Header gray fill
doc.setFillColor(gray,gray,gray);
doc.rect(90,10,220,28,'F');

// Print text
doc.setFontSize(8);
doc.setFontType("normal"); 
lines = doc.splitTextToSize(loremipsum, 105);
doc.text(95, 15, lines);
doc.addImage(logoImg, 'JPEG', 15, 14, 70, 22);


//Title fill
//doc.setFillColor(gray,gray,gray);
//doc.rect(0,123,220,28,'F');
//doc.line(20,35,195,35);

var text = "Training Cognitivo Personalizzato:\nNeuroRehabLab Task Generator";

// Print Header
doc.setFontType("bold"); 
doc.setFontSize(19);
doc.setTextColor(1, 1, 1);
doc.text(20, 110, text);

doc.setFontType("normal"); 
doc.setFontSize(12);
doc.text(20, 130, "Il seguente programma di allenamento cognitivo è stato progettato per...\n\nNome: ___________________________________________________________________");
doc.text(20, 130, "\n\n            " + name);
doc.text(20, 130, "\n\n\n\n(Attenzione: " + attention+", Memoria: "+memory+", Lunzioni Esecutive: " + executivefunctions + ", Linguaggio: "+language+", Difficoltà: "+difficulty+")");
doc.setFontType("normal"); 

//Footer gray fill
doc.setFillColor(gray,gray,gray);
doc.rect(0,280,220,8,'F');
	
//doc.line(20,280,195,280);
doc.setFontSize(12);
doc.text(180, 285, "Pagina " + page);

var now = new Date();
var strDateTime = [[AddZero(now.getDate()), AddZero(now.getMonth() + 1), now.getFullYear()].join("/"), [AddZero(now.getHours()), AddZero(now.getMinutes())].join(":"), now.getHours() >= 12 ? "PM" : "AM"].join(" ");
doc.text(20, 285, "Generato sulla " + strDateTime );

var string = doc.output('datauristring');
$('iframe').attr('src', string);

};

function createFrame(doc, page, model)
{
var gray=230;
//Header gray fill
doc.setFillColor(gray,gray,gray);
doc.rect(120,10,220,28,'F');
doc.addImage(logoImg, 'JPEG', 15, 12, 70, 22);
//doc.line(20,35,195,35);

var text = ["Attenzione:   ","Memoria:    ","Fun. Es.:   ","Lingua: ","Difficoltà:   "];

// Print cognitive training parameters
doc.setFontSize(12);

for (i=0;i<text.length;i++)
    doc.text(145, 16 + i*4.25, text[i]);

/* for (i=0;i<text.length;i++)
{ 
	
	doc.setTextColor(255/8*model[i], 255-155/8*model[i], 0); //set font color to proportional green-red  
    
    doc.text(190, 16 + i*4, model[i]);
	doc.text(165, 16 + i*4, text[i]);
} */

doc.setFontSize(8);
doc.setTextColor(255);
for (i=0;i<text.length;i++)
{ 
	//doc.setDrawColor(0);
	doc.setFillColor(255/8*model[i],255-155/8*model[i],0);
	var size = model[i];
	if (size > 12)
		size = 12;
	doc.rect(170, 12+ i*4.5, 2*size,4,'F');
		
	//doc.setTextColor(255/8*model[i], 255-155/8*model[i], 0); //set font color to red      
    doc.text(170+size/1.5, 15 + i*4.5, model[i]);
}

doc.setTextColor(0, 0, 0);

//Footer gray fill
doc.setFillColor(gray,gray,gray);
doc.rect(0,280,220,8,'F');
	
//doc.line(20,280,195,280);
doc.setFontSize(12);
doc.text(180, 285, "Pagina " + page);

var now = new Date();
var strDateTime = [[AddZero(now.getDate()), AddZero(now.getMonth() + 1), now.getFullYear()].join("/"), [AddZero(now.getHours()), AddZero(now.getMinutes())].join(":"), now.getHours() >= 12 ? "PM" : "AM"].join(" ");
doc.text(20, 285, "Generato sulla " + strDateTime );
doc.setFontSize(8);
doc.text(35, 293, "NeuroRehabLab (c) - http://neurorehabilitation.m-iti.org | Traduzione a cura di Simone Timi e Francesco Semprebon.");

return (doc);

};


function download()
{
	var now = new Date();
	var strDateTime = [[AddZero(now.getDate()), AddZero(now.getMonth() + 1), now.getFullYear()].join("/"), [AddZero(now.getHours()), AddZero(now.getMinutes())].join(":"), now.getHours() >= 12 ? "PM" : "AM"].join("_");
	doc.save("Tasks_"+strDateTime+".pdf");
	
	//var string = doc.output('datauristring');
	//window.location.href="http://neurorehabilitation.m-iti.org/TaskGenerator/dataurldownloader.html?"+string;
};


function generateTraining(name,attention,memory,executivefunctions,language,difficulty,closeMatch)
{
	createCover(name,attention,memory,executivefunctions,language,difficulty);
	
	var clipart = Math.floor(Math.random() * 2);
	
	//Search for optimal image pairs task parameters
	var sum = 0;
	var error = 100000000;
	
	elements = ['8'];
	for (i_elements=1;i_elements<elements;i_elements++)
					{
						model = imagePairsModel(i_elements);
						sum = Math.pow(parseFloat(model[0])-attention,2) + Math.pow(parseFloat(model[1])-memory,2) + Math.pow(parseFloat(model[2])-executivefunctions,2)+Math.pow(parseFloat(model[3])-language,2)+Math.pow(parseFloat(model[4])-difficulty,2);
						if (error > sum)
						{
							closest = [i_elements];
							error = sum;
						} 
					}  
					
	compute = true;
	if (closeMatch)
		if (error > thresholdError)
			compute = false;

	if (compute)
		if (clipart)
			imagePairsTask(parseFloat(closest[0]),1,1);
		else
			imagePairsTask(parseFloat(closest[0]),0,1);
		
	//Search for optimal cancellationTask parameters
	var closest;
	var model;
	var thresholdError = 10;
	var compute;
	
 	var elements = [];
	for (i=3;i<40;i++)
		elements.push(i*i);
	
	var probability = [];
	for (i=5;i<90;i=i+5)
		probability.push(i);

	var numbers = ['0','1','2'];
	var order = ['0','1'];
	
	var size = 15;
	sum = 0;
	error = 100000000;
	
  	for (i_elements=0;i_elements<elements.length;i_elements++)
		for (i_probability=0;i_probability<probability.length;i_probability++)
			for (i_order=0;i_order<order.length;i_order++)
				for (i_numbers=0;i_numbers<numbers.length;i_numbers++)
				{
					model = cancellationModel(elements[i_elements],probability[i_probability],size,numbers[i_numbers],order[i_order]);
					sum = Math.pow(parseFloat(model[0])-attention,2) + Math.pow(parseFloat(model[1])-memory,2) + Math.pow(parseFloat(model[2])-executivefunctions,2)+Math.pow(parseFloat(model[3])-language,2)+Math.pow(parseFloat(model[4])-difficulty,2);
					if (error > sum)
					{
						closest = [elements[i_elements],probability[i_probability],size,numbers[i_numbers],order[i_order]];
						error = sum;
					}
				} 
	
	size = 25 - 0.03*parseFloat(closest[0]);
	
	compute = true;
	if (closeMatch)
		if (error > thresholdError)
			compute = false;

	if (compute)
		cancellationTask(parseFloat(closest[0]),parseFloat(closest[1]),size,parseFloat(closest[3]),parseFloat(closest[4]),1);
	
	//Search for optimal sequencingTask parameters
 	elements = ['12'];
	
	var step = [];
	for (i=1;i<9;i++)
		step.push(i);
	
	var missing = ['1','2','3','4','5'];
	order = ['2','1','0'];
	var where = ['1','0'];
	
	sum = 0;
	error = 100000000;
	
  	for (i_elements=0;i_elements<elements.length;i_elements++)
		for (i_step=0;i_step<step.length;i_step++)
			for (i_where=0;i_where<where.length;i_where++)
				for (i_order=0;i_order<order.length;i_order++)
					for (i_missing=0;i_missing<missing.length;i_missing++)
					{
						model = sequencingModel(elements[i_elements],step[i_step],order[i_order],where[i_where],missing[i_missing])
						sum = Math.pow(parseFloat(model[0])-attention,2) + Math.pow(parseFloat(model[1])-memory,2) + Math.pow(parseFloat(model[2])-executivefunctions,2)+Math.pow(parseFloat(model[3])-language,2)+Math.pow(parseFloat(model[4])-difficulty,2);
						if (error > sum)
						{
							closest = [elements[i_elements],step[i_step],order[i_order],where[i_where],missing[i_missing]];
							error = sum;
						} 
					}  
	
	compute = true;
	if (closeMatch)
		if (error > thresholdError)
			compute = false;

	if (compute)
		sequencingTask(parseFloat(closest[0]),parseFloat(closest[1]),parseFloat(closest[2]),parseFloat(closest[3]),parseFloat(closest[4]),1);
    
    //Search for optimal problem resolution parameters
 	elements = ['10'];
		
	var tens = ['0','1'];
	var explicit = ['0','1'];
	
	sum = 0;
	error = 100000000;
	
  	for (i_elements=2;i_elements<elements;i_elements++)
		for (i_tens=0;i_tens<tens.length;i_tens++)
			for (i_explicit=0;i_explicit<explicit.length;i_explicit++)
					{
						model = problemModel(i_elements,tens[i_tens],explicit[i_explicit])
						sum = Math.pow(parseFloat(model[0])-attention,2) + Math.pow(parseFloat(model[1])-memory,2) + Math.pow(parseFloat(model[2])-executivefunctions,2)+Math.pow(parseFloat(model[3])-language,2)+Math.pow(parseFloat(model[4])-difficulty,2);
						if (error > sum)
						{
							closest = [i_elements,tens[i_tens],explicit[i_explicit]];
							error = sum;
						} 
					}  
	
	compute = true;
	if (closeMatch)
		if (error > thresholdError)
			compute = false;

	if (compute)
		problemTask(parseFloat(closest[0]),parseFloat(closest[1]),parseFloat(closest[2]),1);
    
	//Search for optimal associationTask parameters
	sum = 0;
	error = 100000000;
	
	elements = ['20'];
	for (i_elements=2;i_elements<elements;i_elements++)
					{
						model = associationModel(i_elements);
						sum = Math.pow(parseFloat(model[0])-attention,2) + Math.pow(parseFloat(model[1])-memory,2) + Math.pow(parseFloat(model[2])-executivefunctions,2)+Math.pow(parseFloat(model[3])-language,2)+Math.pow(parseFloat(model[4])-difficulty,2);
						if (error > sum)
						{
							closest = [i_elements];
							error = sum;
						} 
					}  
					
	compute = true;
	if (closeMatch)
		if (error > thresholdError)
			compute = false;

	if (compute)
		if (clipart)
			associationTask_pics(parseFloat(closest[0]),1);
		else
			associationTask(parseFloat(closest[0]),1);


    //Search for optimal associationTask parameters
	sum = 0;
	error = 100000000;
	
	elements = ['10'];
	for (i_elements=3;i_elements<elements;i_elements++)
					{
						model = contextModel(i_elements);
						sum = Math.pow(parseFloat(model[0])-attention,2) + Math.pow(parseFloat(model[1])-memory,2) + Math.pow(parseFloat(model[2])-executivefunctions,2)+Math.pow(parseFloat(model[3])-language,2)+Math.pow(parseFloat(model[4])-difficulty,2);
						if (error > sum)
						{
							closest = [i_elements];
							error = sum;
						} 
					}  
					
	compute = true;
	if (closeMatch)
		if (error > thresholdError)
			compute = false;

	if (compute)
		contextTask(parseFloat(closest[0]),1);

	//Search for optimal word search task parameters
 	elements = ['20'];
	var cues = ['0','1'];
	
	sum = 0;
	error = 100000000;
	
  	for (i_elements=1;i_elements<elements;i_elements++)
			for (i_cues=0;i_cues<cues.length;i_cues++)
					{
						model = soupModel(i_elements,cues[i_cues])
						sum = Math.pow(parseFloat(model[0])-attention,2) + Math.pow(parseFloat(model[1])-memory,2) + Math.pow(parseFloat(model[2])-executivefunctions,2)+Math.pow(parseFloat(model[3])-language,2)+Math.pow(parseFloat(model[4])-difficulty,2);
						if (error > sum)
						{
							closest = [i_elements,cues[i_cues]];
							error = sum;
						} 
					}  
					
	compute = true;
	if (closeMatch)
		if (error > thresholdError)
			compute = false;

	if (compute)
		soupTask(parseFloat(closest[0]),parseFloat(closest[1]),1);
	
	//Search for optimal mazeTask parameters
	sum = 0;
	error = 100000000;
	
	elements = ['11'];
	for (i_elements=3;i_elements<elements;i_elements++)
					{
						model = mazeModel(i_elements);
						sum = Math.pow(parseFloat(model[0])-attention,2) + Math.pow(parseFloat(model[1])-memory,2) + Math.pow(parseFloat(model[2])-executivefunctions,2)+Math.pow(parseFloat(model[3])-language,2)+Math.pow(parseFloat(model[4])-difficulty,2);
						if (error > sum)
						{
							closest = [i_elements];
							error = sum;
						} 
					}  
					
	compute = true;
	if (closeMatch)
		if (error > thresholdError)
			compute = false;

	if (compute)
		mazeTask(parseFloat(closest[0]),1);
	
	//Search for optimal categorization task parameters
 	elements = ['10'];
	var categories = ['10'];
	
	sum = 0;
	error = 100000000;
	
  	for (i_elements=2;i_elements<elements;i_elements++)
			for (i_cues=2;i_cues<categories;i_cues++)
					{
						model = categorizationModel(i_elements,i_cues)
						sum = Math.pow(parseFloat(model[0])-attention,2) + Math.pow(parseFloat(model[1])-memory,2) + Math.pow(parseFloat(model[2])-executivefunctions,2)+Math.pow(parseFloat(model[3])-language,2)+Math.pow(parseFloat(model[4])-difficulty,2);
						
						if (error > sum)
						{
							closest = [i_elements,i_cues];
							error = sum;
						} 
					}  
					
	compute = true;
	if (closeMatch)
		if (error > thresholdError)
			compute = false;

	if (compute)
		categorizationTask(parseFloat(closest[0]),parseFloat(closest[1]),1);
	
	//Search for optimal memory task parameters
 	elements = ['11'];
	categories = ['4'];
	
	sum = 0;
	error = 100000000;
	
  	for (i_elements=1;i_elements<elements;i_elements++)
			for (i_cues=0;i_cues<categories;i_cues++)
					{
						model = storiesModel(i_elements,i_cues)
						sum = Math.pow(parseFloat(model[0])-attention,2) + Math.pow(parseFloat(model[1])-memory,2) + Math.pow(parseFloat(model[2])-executivefunctions,2)+Math.pow(parseFloat(model[3])-language,2)+Math.pow(parseFloat(model[4])-difficulty,2);
						
						if (error > sum)
						{
							closest = [i_elements,i_cues];
							error = sum;
						} 
					}  
					
	compute = true;
	if (closeMatch)
		if (error > thresholdError)
			compute = false;

	if (compute)
		storiesTask(parseFloat(closest[1]),parseFloat(closest[0]),1);
	
	//Search for action sequencing task parameters
 	elements = ['7'];
	categories = ['2'];
	
	sum = 0;
	error = 100000000;
	
  	for (i_elements=2;i_elements<elements;i_elements++)
			for (i_cues=0;i_cues<categories;i_cues++)
					{
						model = actionSequencingModel(i_elements,i_cues)
						sum = Math.pow(parseFloat(model[0])-attention,2) + Math.pow(parseFloat(model[1])-memory,2) + Math.pow(parseFloat(model[2])-executivefunctions,2)+Math.pow(parseFloat(model[3])-language,2)+Math.pow(parseFloat(model[4])-difficulty,2);
						
						if (error > sum)
						{
							closest = [i_elements,i_cues];
							error = sum;
						} 
					}  
					
	compute = true;
	if (closeMatch)
		if (error > thresholdError)
			compute = false;

	if (compute)
		actionSequencingTask(parseFloat(closest[1]),parseFloat(closest[0]),1);
};


 